import firebase from './firebase'

let host = 'http://localhost:3000/posts'

export const getPosts=()=>{
    return axios.get(host)
        .then(r=>{
            return r.data
        }).catch(e=>{
            return e.response
        })
}

export const postPost=(post)=>{
    return axios.post(host, post)
        .then(r=>{
